import 'bootstrap/dist/css/bootstrap.css';
import App, { Container } from 'next/app';
import React from 'react';
import { wrapper } from '../redux/index.js';
import { Provider } from 'react-redux';
// import Store from '../redux/index.js'


import NavBar from '../components/NavBar';

//====================
import { createStore, applyMiddleware, compose } from 'redux';
import { createWrapper } from 'next-redux-wrapper';
import rootReducer from '../redux/reducers/index.js';
import thunk from 'redux-thunk';
import { createFirestoreInstance, reduxFirestore, getFirestore } from 'redux-firestore';
import { ReactReduxFirebaseProvider, reactReduxFirebase, getFirebase } from 'react-redux-firebase';
import fbConfig from '../lib/database/config/fbConfig.js';
import firebase from 'firebase/app'
import 'firebase/auth';
import 'firebase/database';
import 'firebase/firestore';

// create a makeStore function

const initialState = {};
// const middleware = [thunk];

// export const store = () => createStore(rootReducer,
//   initialState,
//   compose(
//       applyMiddleware(thunk.withExtraArgument({ getFirebase, getFirestore })),
//       reduxFirestore(fbConfig)
//   )
// );
  if (!firebase.apps.length) {
    firebase.initializeApp(fbConfig);
  }

firebase.firestore();

const rfConfig = {
  userProfile: 'users',
  useFirestoreForProfile: true
}
const rrfProps = {
  firebase,
  config: rfConfig,
  dispatch: store.dispatch,
  createFirestoreInstance,
} 

const createStoreWithFirebase = compose(
  applyMiddleware(thunk.withExtraArgument({ getFirebase, getFirestore })),
  reduxFirestore(firebase, rfConfig)
)(createStore)

const store = () => createStoreWithFirebase(rootReducer, initialState)

//=====================
function MyApp({ Component, pageProps }) {
  // const store = Store()

  return (
    <Provider store={store}>
      <div>
        <NavBar />
        <Component {...pageProps} />
        <style jsx>{`
          body {
            margin: 0px;
            padding: 0px;
          }
        `}</style>
      </div>
    </Provider>
  );
}

// Only uncomment this method if you have blocking data requirements for
// every single page in your application. This disables the ability to
// perform automatic static optimization, causing every page in your app to
// be server-side rendered.
//
// MyApp.getInitialProps = async (appContext) => {
//   // calls page's `getInitialProps` and fills `appProps.pageProps`
//   const appProps = await App.getInitialProps(appContext);

//   return { ...appProps }
// }

export default wrapper.withRedux(MyApp);
