import { createStore, applyMiddleware, compose } from 'redux';
import { createWrapper } from 'next-redux-wrapper';
import storeReducers from '../reducers/index'
import thunk from 'redux-thunk'
// create a makeStore function

const initialState = {};
const middleware = [thunk]

const makeStore = (context) => createStore(storeReducers, initialState, compose(applyMIddleware(...middleware), window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()));

// export an assembled wrapper
export const wrapper = createWrapper(makeStore, { debug: true });
