// export default getDataClient;

const getFirestoreData = () => {
  return async (dispatch, getState) => {
    //make async call to db
    let firebase = await loadFirebase();
    let result = await new Promise((resolve, reject) => {
      firebase
        .firestore()
        .collection('products')
        .get()
        .then((snapshot) => {
          let data = [];
          snapshot.forEach((doc) => {
            data.push(
              Object.assign(
                {
                  id: doc.id
                },
                doc.data()
              )
            );
          });
          resolve(data);
        })
        .catch((error) => {
          console.log(error);
          reject([]);
        });
    });
    dispatch({type: 'GET_FIRESTORE_DATA', result})
  }
}

export default getFirestoreData;
