import Router from 'next/router';
import axios from 'axios';
import { GET_DATA_SERVER, GET_DATA_CLIENT } from '../types/types';
import firebase from '../../lib/database/index'

export const getDataClient = () => (dispatch) => {
  async function getData() {
    const storeData = await firebase.collection('products').get();
    const data = []
    storeData.forEach((doc) => {
      data.push(doc);
    });
    dispatch({
      type: GET_DATA_CLIENT,
      payload: data
    })
  }
}
