import React from 'react';
import ShopDisplay from '../../components/shop/ShopDisplay';
import {loadFirebase} from '../../lib/database/config/fbConfig.js';
import { connect } from 'react-redux';
import action from '../../redux/actions/index.js'
import Router from 'next/router'
import getFirestoreData from '../../redux/actions/storeActions.js'

class Shop extends React.Component {
  constructor(props){
    super(props);
  }
  //gets initial data from firestore and stores it into props
  static async getInitialProps() {
    let firebase = await loadFirebase();
    let result = await new Promise((resolve, reject) => {
      firebase
        .firestore()
        .collection('products')
        .get()
        .then((snapshot) => {
          let data = [];
          snapshot.forEach((doc) => {
            data.push(
              Object.assign(
                {
                  id: doc.id
                },
                doc.data()
              )
            );
          });
          resolve(data);
        })
        .catch((error) => {
          console.log(error);
          reject([]);
        });
    });
    return { products: result };
  }

  render() {
    const products = this.props.products;
    console.log('products', products);
    return (
      <div>
        Shop route page
        <ShopDisplay products={products}/>
      </div>
    );
  }
}

// to use an action, need to mapDispatchToProps and bring in the action to use from props video below at 7:22
//https://www.youtube.com/watch?v=apg98RIJfJo&list=PL4cUxeGkcC9iWstfXntcj8f-dFZ4UtlN3&index=14
export default Shop;
